


















USE ehotelsdb;


CREATE VIEW ViewAvailableRoomsPerAreaView AS
SELECT 
	TRIM(SUBSTRING_INDEX(h.address, ',', -1)) AS location, -- get city/country part after comma
    COUNT(r.room_id) AS available_rooms
FROM hotel AS h
JOIN room AS r ON h.hotel_ID = r.hotel_ID
WHERE r.room_status = 'available'
GROUP BY location;



CREATE VIEW ViewHotelTotalCapacityView AS
SELECT h.hotel_ID, SUM(r.capacity) AS total_capacity
FROM hotel AS h
JOIN room AS r ON h.hotel_ID = r.hotel_ID
GROUP BY h.hotel_ID;



CREATE INDEX idx_rating ON hotel (rating)
CREATE INDEX idx_hotel_chain_id ON hotel (hotel_chain_ID)
CREATE INDEX idx_length_stay ON booking (length_stay)


DELIMITER $$

CREATE TRIGGER CheckIDLengthBeforeInsertEmployee
BEFORE INSERT ON employee
FOR EACH ROW
BEGIN
   IF CHAR_LENGTH(NEW.id) < 5 THEN
     SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Error: Minimum characters in ID field is 5.';
   END IF;
END$$

DELIMITER ;

